import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from './Navbar.module.css';
import { FaGlobeAmericas, FaHome } from 'react-icons/fa';
import { AuthContext } from '../../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 10);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  return (
    <nav className={`${styles.navbar} ${scrolled ? styles.scrolled : ''}`}>
      <div className={styles.logo}>
        <FaGlobeAmericas className={styles.icon} />
        <Link to="/">Trip Me Up</Link>
      </div>

      <div className={styles.links}>
        {/* Only show these when logged in */}
        {user && (
          <>
            <Link to="/cities" className={styles.link}>
              Cities
            </Link>
            <Link to="/restaurants" className={styles.link}>
              Restaurants
            </Link>
            <Link to="/accommodations" className={styles.link}>
              Accommodations
            </Link>
            <Link to="/bookings" className={styles.link}>
              My Bookings
            </Link>
            <Link to="/favorites" className={styles.link}>
              Favorites
            </Link>
          </>
        )}
      </div>

      <div className={styles.actions}>
        {!user && (
          <Link to="/" className={styles.homeIcon}>
            <FaHome />
          </Link>
        )}

        {user ? (
          <>
            <span className={styles.greeting}>Hi, {user.username}</span>
            <button className={`${styles.btn} ${styles.primary}`} onClick={logout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className={`${styles.btn} ${styles.outline}`}>
              Login
            </Link>
            <Link to="/register" className={`${styles.btn} ${styles.primary}`}>
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
